package task_1_2_2;

import org.checkerframework.checker.formatter.qual.ReturnsFormat;

import java.util.Iterator;
import java.util.LinkedList;
import java.util.List;

public class Tree<Node> implements Iterable<Node>{
    @Override
    public Iterator<Node> iterator() {
        return new BreadthFirstSearchIterator();
    }

    class BreadthFirstSearchIterator implements Iterator<Node> {
        LinkedList<Node> queue = new LinkedList<>();

        @Override
        public boolean hasNext() {
            if (queue.size() == 0) {
                return false;
            }
            return true;
        }

        @Override
        public Node next() {
            return queue.poll();
        }

        @Override
        public void remove() {
            Iterator.super.remove();
        }
    }

    class Node<T> {
        T value;
        Node<T> parent;
        List<Node<T>> children;

        public Node(T value) {
            this.value = value;
            this.children = new LinkedList<>();
        }

        private void addChild(Node child) {
            child.parent = this;
            children.add(child);
        }
    }

    Node root;

    private Node breadthFirstSearch(T value) {
        LinkedList<Node> queue = new LinkedList<>();
        queue.add(root);

        while (queue.size() != 0) {
            Node currentNode = queue.poll();
            if (currentNode.value.equals(value)) {
                return currentNode;
            }

            for (int i = 0; i < currentNode.childrenLength; i++) {
                queue.add(currentNode.children[i]);
            }
        }

        return null;
    }

    public void add(String value) {
        if (root.value == null) {
            root.value = value;
        } else {
            root.addChild(new Node(value));
        }
    }

    public void add(Node parent, String value) {
        parent.addChild(new Node<>(value));
    }
}
